document.addEventListener("scroll",function(){const t=document.getElementById("pe-comments"),e=document.getElementById("comments-link"),n=window.innerHeight||document.documentElement.clientHeight||document.body.clientHeight,s=t.offsetTop,o=document.documentElement.scrollTop,i=s-o;i<=n+100?(e.style.visibility="hidden",e.style.opacity="0"):(e.style.visibility="visible",e.style.opacity="1")})