let comment=document.getElementById("pe-comments"),bottomToComment=document.getElementById("comments-link");document.addEventListener("scroll",function(){const e=window.innerHeight||document.documentElement.clientHeight||document.body.clientHeight,t=comment.offsetTop,n=document.documentElement.scrollTop,s=t-n;s<=e+100?(bottomToComment.style.visibility="hidden",bottomToComment.style.opacity="0"):(bottomToComment.style.visibility="visible",bottomToComment.style.opacity="1")})